var action = function () {
	TweenMax.to($('.page-content-wrapper'), 2.5, {css:{rotationY: '180deg'}, ease:Bounce.easeOut});
	$('body').append('<a href="http://www.revolution9.fr/" target="_blank" title="Crédits : Révolution 9" id="r9" style="width: 30px; position:fixed; right: 15px; bottom : 50px; z-index: 10000"><img src="data:image/png;base64," alt=""/></a>');
	TweenMax.from($('#r9'), 3, {css:{bottom: '150%'}, delay: 3, ease:Bounce.easeOut});
};

jQuery(function(){
	if (! window.TweenMax) {
		var s = document.createElement("script");
	    s.type = "text/javascript";
	    s.src = "https://cdnjs.cloudflare.com/ajax/libs/gsap/1.18.0/TweenMax.min.js";
	    $("head").append(s);
	}


	var k = [38, 38, 40, 40, 37, 39, 37, 39, 66, 65],
	n = 0;
	$(document).keydown(function (e) {
		if (e.keyCode === k[n++]) {
			if (n === k.length) {
				action();
				return !1
			}
		} else k = 0
	});
});
