(function($) {
  $.fn.cnilCookies = function(options) {

    //On définit nos paramètres par défaut
    var defauts = {
      "expireCookie": 30,
      "nameCookie": 'cnilCookie'
    };

    //On fusionne nos deux objets
    var parameters = $.extend(defauts, options);


    ///Nos fonctions de gestion des cookies
    var createCookie = function(name, value, days) {
      if (days) {
        var date = new Date();
        date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
        var expires = "; expires=" + date.toGMTString();
      } else {
        var expires = "";
      }

      document.cookie = name + "=" + value + expires + "; path=/";
    }

    var readCookie = function(name) {
      var nameEQ = name + "=";
      var ca = document.cookie.split(';');
      for (var i = 0; i < ca.length; i++) {
        var c = ca[i];
        while (c.charAt(0) == ' ') c = c.substring(1, c.length);
        if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
      }
      return null;
    }

    var eraseCookie = function(name) {
      createCookie(name, "", -1);
    }

    var $this = $(this);

    return this.each(function() {
      if (readCookie(parameters.nameCookie) == null) {
        createCookie(parameters.nameCookie, '1', parameters.expireCookie);
        $this.show();
      }

      
    });
  };
  $(document).ready(function() {
    $('#infocookie').cnilCookies({
      'expireCookie': 30
    });

    var htmlPopinCookies = $('#infocookie').clone();
    $('#infocookie').remove();
    $('body').prepend(htmlPopinCookies);
    $('body').on('click', '#infocookie .close', function() {
    	$(this).closest('#infocookie').slideUp('fast');
		return false;
	});

  });
})(jQuery);